// DetailsPage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';

function DetailsPage() {
  const { id } = useParams();
  const [vehicle, setVehicle] = useState(null);
  const [comment, setComment] = useState('');

  useEffect(() => {
    document.title = 'Vehicle Details';
    console.log("Fetching details for ID:", id);
    fetch(`http://127.0.0.1:8000/api/vehicles/${id}/`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log("Received data:", data);
        setVehicle(data);
      })
      .catch((error) => {
        console.error('Error fetching vehicle details:', error);
      });
  }, [id]);


  const handleCommentSubmit = () => {
    // Assuming you have an endpoint to update the extra_data field.
    const commentData = {
      ...vehicle,
      extra_data: {
        ...vehicle.extra_data,
        [new Date().toISOString()]: comment,
      },
    };

    fetch(`http://127.0.0.1:8000/api/vehicles/${id}/`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(commentData),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Comment added successfully:', data);
        setVehicle(data);
        setComment('');
      })
      .catch((error) => {
        console.error('Error adding comment:', error);
      });
  };


  if (!vehicle) {
    return <div>Loading...</div>;
  }

  return (
    <div>
    <Navbar expand="lg" className="bg-body-tertiary" data-bs-theme="dark">
        <Container fluid>
          <Navbar.Brand href="http://localhost:3000">VehicleVerse</Navbar.Brand>
          <Navbar.Toggle aria-controls="vehicleverse" />
          <Navbar.Collapse id="vehicleverse">
            <Nav className="me-auto my-2 my-lg-0" style={{ maxHeight: '100px' }}>
              <Nav.Link href="http://localhost:3000">Home</Nav.Link>
              <Nav.Link href="http://localhost:3000/rental">Rent</Nav.Link>
              <NavDropdown title="POST" id="navbarScrollingDropdown">
                <NavDropdown.Item
                  href="http://localhost:8000/admin/marketplace/vehicle/add/"
                  target="_blank"  // This opens the link in a new window
                >
                  ADD For Sell
                </NavDropdown.Item>
                <NavDropdown.Item
                  href="http://localhost:8000/admin/marketplace/rental/add/"
                  target="_blank"  // This opens the link in a new window
                >
                  ADD For Rent
                </NavDropdown.Item>
              </NavDropdown>
            </Nav>
            <Button variant="danger" href="http://localhost:3000/s/home">Log Out</Button>
          </Navbar.Collapse>
        </Container>
      </Navbar>


    <Container className="mt-4">
        <Row className="justify-content-md-center">
          <Col lg={12}>
            <Card className="shadow" style={{ width: '125%', marginLeft: '-10%', marginRight: '-10%' }}>
              <Row>
                <Col md={6}>
                  <Card.Img src={vehicle.image} alt="Vehicle" />
                </Col>
                <Col md={6}>
                  <Card.Body>
                    <Card.Title className="text-center mb-4"><h1 className="text-center">Vehicle Details</h1></Card.Title>
                    <div className="mb-3" style={{ fontSize: '1.4em' }}>
                      <strong>Vehicle Type:</strong> {vehicle.vehicle_type}
                    </div>
                    <div className="mb-3" style={{ fontSize: '1.4em' }}>
                      <strong>Make:</strong> {vehicle.make}
                    </div>
                    <div className="mb-3" style={{ fontSize: '1.4em' }}>
                      <strong>Model:</strong> {vehicle.model}
                    </div>
                    <div className="mb-3" style={{ fontSize: '1.4em' }}>
                      <strong>Year:</strong> {vehicle.year}
                    </div>
                    <div className="mb-3" style={{ fontSize: '1.4em' }}>
                      <strong>Price:</strong> {vehicle.price}
                    </div>
                    <div className="mb-3" style={{ fontSize: '1.4em' }}>
                      <strong>Phone Number:</strong> {vehicle.phone_number}
                    </div>
                    <div className="mb-3" style={{ fontSize: '1.4em' }}>
                      <strong>Description:</strong> {vehicle.description}
                    </div>

                  </Card.Body>
                </Col>
              </Row>
            </Card>
          </Col>
        </Row>
      </Container>
      <Container className="mt-4">
        <Row className="justify-content-md-center">

          <Col lg={6}>
            <Card className="shadow">
              {/* Comment Section Card */}
              <Card.Body>
                <h2>Comments</h2>
                <textarea
                  rows="4"
                  cols="50"
                  value={comment}
                  onChange={(e) => setComment(e.target.value)}
                ></textarea>
                <Button variant="primary" onClick={handleCommentSubmit}>
                  Add Comment
                </Button>
                <div>
                  {Object.entries(vehicle.extra_data || {}).map(([timestamp, comment]) => (
                    <div key={timestamp}>
                      <strong>{new Date(timestamp).toLocaleString()}:</strong> {comment}
                    </div>
                  ))}
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default DetailsPage;
