// DetailsPage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';

function DetailsPage() {
  const { id } = useParams();
  const [vehicle, setVehicle] = useState(null);

  useEffect(() => {
    console.log("Fetching details for ID:", id);
    fetch(`http://127.0.0.1:8000/api/vehicles/${id}/`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log("Received data:", data);
        setVehicle(data);
      })
      .catch((error) => {
        console.error('Error fetching vehicle details:', error);
      });
  }, [id]);


  if (!vehicle) {
    return <div>Loading...</div>;
  }

  return (
    <Container className="mt-4">
      <h1 className="text-center">Vehicle Details</h1>
      <Row>
        <Col md={4}>
          <Card className="mb-4 shadow">
            <Card.Img src={vehicle.image_link} alt="Vehicle" style={{ height: '320px' }} />
            <Card.Body>
              <Card.Title>Vehicle Type: {vehicle.vehicle_type}</Card.Title>
              <Card.Text>Make: {vehicle.make}</Card.Text>
              <Card.Text>Model: {vehicle.model}</Card.Text>
              <Card.Text>Year: {vehicle.year}</Card.Text>
              <Card.Text>Phone Number: {vehicle.phone_number}</Card.Text>
              <Card.Text>Price: {vehicle.price}</Card.Text>
              <Card.Text>Description: {vehicle.description}</Card.Text>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default DetailsPage;
