import React, { useState } from 'react';
import Axios from 'axios';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';

function RentPost() {
  const [formData, setFormData] = useState({
    vehicle_type: 'car', // Default vehicle type
    phone_number: '',
    model: '',
    price: 0,
    description: 'No description available',
    is_available: true,
    image_link: '',
  });

  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    Axios.post('http://127.0.0.1:8000/api/rentals/create', formData)
      .then((response) => {
        // Handle success
        setSuccessMessage('Rental post created successfully');
        // Reset the form or perform any other necessary actions
        setFormData({
          vehicle_type: 'car',
          phone_number: '',
          model: '',
          price: 0,
          description: 'No description available',
          is_available: true,
          image_link: '',
        });
      })
      .catch((error) => {
        // Handle error
        console.error('Error creating rental post:', error);
        if (error.response && error.response.data) {
          setErrorMessage(error.response.data);
        } else {
          setErrorMessage('An error occurred while creating the rental post.');
        }
      });
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div>
      <Navbar expand="lg" className="bg-body-tertiary">
        {/* ... your Navbar code ... */}
      </Navbar>

      <Container className="mt-4">
        <h1 className="text-center">Create a Rental Post</h1>
        {successMessage && <div className="text-success">{successMessage}</div>}
        {errorMessage && <div className="text-danger">{errorMessage}</div>}
        <Form onSubmit={handleSubmit}>
          {/* ... your Form code ... */}
        </Form>
      </Container>
    </div>
  );
}

export default RentPost;
