// RentalDetailsPage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';

function RentalDetailsPage() {
  const { id } = useParams();
  const [rentalDetails, setRentalDetails] = useState(null);

  useEffect(() => {
    // Fetch detailed information for the specific rental using the ID
    // Replace the URL with your actual API endpoint for fetching rental details
    fetch(`http://127.0.0.1:8000/api/rentals/${id}/`)
      .then((response) => response.json())
      .then((data) => setRentalDetails(data))
      .catch((error) => console.error('Error fetching rental details:', error));
  }, [id]);

  if (!rentalDetails) {
    return <div>Loading...</div>;
  }

  return (
    <Container className="mt-4">
      <h1 className="text-center">Rental Details</h1>
      <Row>
        <Col md={4}>
          <Card className="mb-4 shadow">
            <Card.Img src={rentalDetails.image_link} alt="Rental" style={{ height: '320px' }} />
            <Card.Body>
              <Card.Title>{rentalDetails.vehicle_type}</Card.Title>
              <Card.Text>Model: {rentalDetails.model}</Card.Text>
              <Card.Text>Phone Number: {rentalDetails.phone_number}</Card.Text>
              <Card.Text>Description: {rentalDetails.description}</Card.Text>
              <Card.Text>Price: {rentalDetails.price}</Card.Text>
              <Card.Text>Is Available: {rentalDetails.is_available ? 'Yes' : 'No'}</Card.Text>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default RentalDetailsPage;
